$(function(){var e=$(".settings-panel"),t=$("#save-settings");$("#edit-settings").on("click",function(){e.toggleClass("open")});$(".close-sidebar").on("click",function(){e.removeClass("open")});t.on("click",function(){setTimeout(function(){t.removeClass("success").attr("value","Save settings");e.removeClass("open")},2e3)});$("#add-website").on("click",function(){$(".dark-panel").toggleClass("open")});$("#create-website").on("click",function(){setTimeout(function(){$("#create-website").removeClass("success").attr("value","Create website");$(".dark-panel").removeClass("open");location.reload()},2e3)})});$("#save-settings").on("click",function(){var e=$("#update-settings").serialize();$.ajax({type:"POST",url:"lola/init.lola.php",data:e,success:function(e,t,n){n.getResponseHeader("settingsSaved")==1?$("#save-settings").addClass("success").attr("value","Settings saved"):$("#save-settings").addClass("error").attr("value",n.getResponseHeader("settingsSaved"))}});return!1});$("#create-website").on("click",function(){var e=$("#process-website").serialize();$.ajax({type:"POST",url:"lola/init.lola.php",data:e,success:function(e,t,n){n.getResponseHeader("createWebsite")==1?$("#create-website").addClass("success").attr("value","Website created"):$("#create-website").addClass("error").attr("value",n.getResponseHeader("websiteCreated"))}});return!1});